import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import '../styles/style.css';

const LanguagePage = () => {
    const { lang } = useParams();
    const [songs, setSongs] = useState([]);
    const [currentSongIndex, setCurrentSongIndex] = useState(0); // Track the current song index
    const [audioElement, setAudioElement] = useState(null);
    const [isPlaying, setIsPlaying] = useState(false);

    useEffect(() => {
        axios.get(`http://localhost:5000/songs?lang=${lang}`)
            .then(response => {
                setSongs(response.data);
                if (response.data.length > 0) {
                    setCurrentSongIndex(0);
                    const newAudio = new Audio(`http://localhost:5000/public/${response.data[0].path}`);
                    setAudioElement(newAudio);
                }
            })
            .catch(error => console.error('Error fetching songs:', error));
    }, [lang]);

    // Play a selected song
    const playSong = (song) => {
        if (audioElement) audioElement.pause();
        
        const newAudio = new Audio(`http://localhost:5000/public/${song.path}`);
        newAudio.play();
        setAudioElement(newAudio);
        setIsPlaying(true);
    };

    // Toggle play/pause of the song
    const togglePlayPause = () => {
        if (audioElement) {
            if (isPlaying) {
                audioElement.pause();
                setIsPlaying(false);
            } else {
                audioElement.play();
                setIsPlaying(true);
            }
        }
    };

    // Toggle play/pause for individual song
    const toggleSongPlayPause = (song) => {
        if (audioElement && songs[currentSongIndex] !== song) {
            audioElement.pause();
            setIsPlaying(false);
            playSong(song);
        } else if (audioElement && songs[currentSongIndex] === song) {
            if (isPlaying) {
                audioElement.pause();
                setIsPlaying(false);
            } else {
                audioElement.play();
                setIsPlaying(true);
            }
        }
    };

    // Handle next song
    const playNextSong = () => {
        const nextIndex = (currentSongIndex + 1) % songs.length;
        setCurrentSongIndex(nextIndex);
        playSong(songs[nextIndex]);
    };

    // Handle previous song
    const playPrevSong = () => {
        const prevIndex = (currentSongIndex - 1 + songs.length) % songs.length;
        setCurrentSongIndex(prevIndex);
        playSong(songs[prevIndex]);
    };

    return (
        <div>
            <div className="container">
                <div className="songList">
                    <h1>{lang.toUpperCase()} SONGS</h1>
                    <div className="songItemContainer">
                        {songs.map((song, index) => (
                            <div className="songItem" key={song._id}>
                                <img src={`http://localhost:5000/public/${song.image}`} alt="Album Cover" />
                                <span className="songName">Song {index + 1}</span> {/* Displaying Song 1, Song 2... */}
                                <span className="songArtist"></span>
                                <span className="songlistplay">
                                    <span className="timestamp">
                                        <SongDuration path={song.path} />
                                        <i className={`far songItemPlay fa-${currentSongIndex === index && isPlaying ? 'pause' : 'play'}-circle`} 
                                           onClick={() => toggleSongPlayPause(song)}></i>
                                    </span>
                                </span>
                            </div>
                        ))}
                    </div>
                </div>
                <div className="songBanner"></div>
            </div>
            <div className="bottom">
                <input type="range" name="range" id="myProgressBar" min="0" value="0" max="100" />
                <div className="icons">
                    <i className="fas fa-3x fa-step-backward" id="previous" onClick={playPrevSong}></i>
                    <i className={`fas fa-3x fa-${isPlaying ? 'pause' : 'play'}-circle`} id="masterPlay" onClick={togglePlayPause}></i>
                    <i className="fas fa-3x fa-step-forward" id="next" onClick={playNextSong}></i>
                </div>
                <div className="songInfo">
                    <img src="/assets/playing.gif" width="42px" alt="" id="gif" />
                    <span id="masterSongName">{songs[currentSongIndex] ? `Song ${currentSongIndex + 1}` : "Song 1"}</span>
                </div>
            </div>
        </div>
    );
};

// A new component to calculate the song's duration and display it
const SongDuration = ({ path }) => {
    const [duration, setDuration] = useState('00:00');

    // Function to get the duration of a song using the Audio object
    const getSongDuration = (songPath) => {
        return new Promise((resolve) => {
            const audio = new Audio(`http://localhost:5000/public/${songPath}`);
            audio.onloadedmetadata = () => {
                const durationInSeconds = audio.duration;
                const minutes = Math.floor(durationInSeconds / 60);
                const seconds = Math.floor(durationInSeconds % 60);
                const duration = `${minutes}:${seconds < 10 ? '0' : ''}${seconds}`;
                resolve(duration);
            };
        });
    };

    useEffect(() => {
        getSongDuration(path).then((calculatedDuration) => {
            setDuration(calculatedDuration);
        });
    }, [path]);

    return <span>{duration}</span>;
};

export default LanguagePage;
