import React, { useState } from 'react';
import '../styles/login.css';  // Ensure your CSS file is correctly linked

const LoginSignup = () => {
    const [isLogin, setIsLogin] = useState(true);

    const toggleForm = () => {
        setIsLogin(!isLogin);
    };

    const handleLogin = (event) => {
        event.preventDefault();
        const phone = event.target.loginPhone.value;
        const password = event.target.loginPassword.value;
        localStorage.setItem('username', phone); // Store phone number as username
        alert(`Logged in as: ${phone}`);
        window.location.href = '/home'; // Redirect to the main page after login
    };

    const handleSignup = (event) => {
        event.preventDefault();
        const name = event.target.signupName.value;
        const phone = event.target.signupPhone.value;
        const otp = event.target.otp.value;
        const password = event.target.signupPassword.value;

        // Here, you would typically send this data to your server
        localStorage.setItem('username', phone); // Store phone number as username
        alert(`Signed up as: ${name} (${phone})`);
        window.location.href = '/home'; // Redirect to the main page after signup
    };

    return (
        <div className="container">
            <div className="form-container">
                <div className="form-toggle">
                    <button onClick={toggleForm} className={`toggle-button ${isLogin ? 'active' : ''}`}>Login</button>
                    <button onClick={toggleForm} className={`toggle-button ${!isLogin ? 'active' : ''}`}>Signup</button>
                </div>

                {isLogin ? (
                    <form id="loginForm" className="form" onSubmit={handleLogin}>
                        <h2>Login</h2>
                        <input type="text" name="loginPhone" placeholder="Phone Number" required />
                        <input type="password" name="loginPassword" placeholder="Password" required />
                        <button type="submit">Login</button>
                    </form>
                ) : (
                    <form id="signupForm" className="form" onSubmit={handleSignup}>
                        <h2>Signup</h2>
                        <input type="text" name="signupName" placeholder="Name" required />
                        <input type="text" name="signupPhone" placeholder="Phone Number" required />
                        <input type="text" name="otp" placeholder="OTP" required />
                        <input type="password" name="signupPassword" placeholder="Password" required />
                        <button type="submit">Signup</button>
                    </form>
                )}
            </div>
        </div>
    );
};

export default LoginSignup;
