import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import '../styles/style.css';  // Import your CSS file

const LanguagePage = () => {
    const { lang } = useParams();  // Extract language from URL parameters
    const [songs, setSongs] = useState([]);
    const [currentSong, setCurrentSong] = useState(null);
    const [audioElement, setAudioElement] = useState(null);
    const [isPlaying, setIsPlaying] = useState(false);

    useEffect(() => {
        fetch(`http://localhost:5000/songs?lang=${lang}`)
            .then(response => response.json())
            .then(data => {
                setSongs(data);
                if (data.length > 0) {
                    setCurrentSong(data[0]);
                    setAudioElement(new Audio(data[0].path));
                }
            })
            .catch(error => console.error('Error fetching songs:', error));
    }, [lang]);

    const playSong = (song) => {
        if (audioElement) {
            audioElement.pause();
        }
        const newAudioElement = new Audio(song.path);
        setAudioElement(newAudioElement);
        setCurrentSong(song);
        newAudioElement.play();
        setIsPlaying(true);
    };

    const togglePlayPause = () => {
        if (audioElement) {
            if (isPlaying) {
                audioElement.pause();
                setIsPlaying(false);
            } else {
                audioElement.play();
                setIsPlaying(true);
            }
        }
    };

    return (
        <div>
            <nav>
                <ul>
                    <li className="brand"><img src="/assets/logo.png" alt="Spotify" />Spotify</li>
                    <li className="nav-text">EMBRACE THE MUSIC</li>
                    <li><a href="/" className="nav-button">Home</a></li>
                    <li><a href="/about" className="nav-button">About</a></li>
                    <li><a href="/login" className="nav-button" id="loginButton">Login</a></li>
                </ul>
            </nav>
            <div className="container">
                <div className="songList">
                    <h1>{lang.toUpperCase()} SOUNDS</h1>  {/* Display language name */}
                    <div className="songItemContainer">
                        {songs.map((song, index) => (
                            <div className="songItem" key={index}>
                                <img src={song.image} alt="Album Cover" />
                                <span className="songName">{song.name}</span>
                                <span className="songlistplay">
                                    <span className="timestamp">{song.duration} 
                                        <i className={`far songItemPlay fa-${currentSong === song && isPlaying ? 'pause' : 'play'}-circle`} 
                                           onClick={() => playSong(song)}></i>
                                    </span>
                                </span>
                            </div>
                        ))}
                    </div>
                </div>
                <div className="songBanner"></div>
            </div>
            <div className="bottom">
                <input type="range" name="range" id="myProgressBar" min="0" value="0" max="100" />
                <div className="icons">
                    <i className="fas fa-3x fa-step-backward" id="previous"></i>
                    <i className={`fas fa-3x fa-${isPlaying ? 'pause' : 'play'}-circle`} id="masterPlay" onClick={togglePlayPause}></i>
                    <i className="fas fa-3x fa-step-forward" id="next"></i>
                </div>
                <div className="songInfo">
                    <img src="/assets/playing.gif" width="42px" alt="" id="gif" />
                    <span id="masterSongName">{currentSong ? currentSong.name : "Song1"}</span>
                </div>
            </div>
        </div>
    );
};

export default LanguagePage;
